mapper.update:
update dogather.group
set updated = current_timestamp(), deadline = #{deadline}, max_people = #{maxPeople}, end = #{end}, status = #{status}
where group_no = #{groupNo}

mapper.delete:
delete from dogather.user_group
where user_no = #{userNo}
  and group_leader = #{groupLeader}
  and group_no = #{groupNo}
  and product_no = #{productNo}

mapper.select:
select p.product_name, p.product_price, g.max_people, g.deadline
  from p.group_no = g.group_no
and p.group_no = #{groupNo};
